import React from 'react';

const Ex3 = () => {
  return <div>
    <p>Réalisez une application permettant d'afficher une liste d'éléments.</p>
    <p>Votre liste de courses sera stockée (mais pas sauvegardée) dans un state qui contiendra un tableau.</p>
    <p>Un champ texte permettra d'ajouter un élément dans la liste</p>
  </div>;
};

export default Ex3;
