const Ex1 = () => {
  return <div>
    <p>Modifiez le projet précédent pour ajouter un deuxième champ texte pour le prénom. L'application doit afficher les deux champs texte et les réafficher après 'Bonjour'.</p>
    <input type="text" name="nom" id="nom" />
    <input type="text" name="prenom" id="prenom" />

    <div>Bonjour </div>
    </div>;
};

export default Ex1;
